				}
				else if(selectedValue == "PH"){
					div_defaultdis.style.display = "none";
					div_russia.style.display = "none";
					div_korea.style.display = "none";
                    div_japan.style.display="none";
                  	div_ph.style.display="block";
					RU_CHK1.disable();
					RU_CHK2.disable();
					JP_CHK1.disable();
					KO_CHK1.disable();
					KO_CHK2.disable();
					KO_CHK3.disable();
					KO_CHK4.disable();
